<?php
include_once APP . 'Model/WorkflowModules/WorkflowBaseModule.php';

class Module_reset_event extends WorkflowBaseActionModule
{
    public $blocking = false;
    public $disabled = true;
    public $id = 'excercise-reset-event';
    public $name = 'Excercise :: Reset Event';
    public $description = 'Reset the event to be evaluated';
    public $icon = 'tasks';
    public $inputs = 1;
    public $outputs = 1;
    public $expect_misp_core_format = true;
    public $params = [];

    protected $Event;
    protected $EventBlocklist;

    public function exec(array $node, WorkflowRoamingData $roamingData, array &$errors = []): bool
    {
        parent::exec($node, $roamingData, $errors);
        $this->Event = ClassRegistry::init('Event');
        $this->EventBlocklist = ClassRegistry::init('EventBlocklist');

        $this->resetEvent($roamingData);
        $roamingData->__secret = '__secret_key__';
        return true;
    }

    public function resetEvent(WorkflowRoamingData $roamingData)
    {
        $event = $roamingData->getData();
        $this->Event->quickDelete($event);
        $this->EventBlocklist->deleteAll([
            'EventBlocklist.event_uuid' => $event['Event']['uuid'],
        ]);
        $NoteModel = ClassRegistry::init('Note');
        $OpinionModel = ClassRegistry::init('Opinion');
        $RelationshipModel = ClassRegistry::init('Relationship');
        $NoteModel->deleteAll([
            'Note.object_uuid' => $event['Event']['uuid'],
            'Note.object_type' => 'Event'
        ]);
        $OpinionModel->deleteAll([
            'Opinion.object_uuid' => $event['Event']['uuid'],
            'Opinion.object_type' => 'Event'
        ]);
        $RelationshipModel->deleteAll([
            'Relationship.object_uuid' => $event['Event']['uuid'],
            'Relationship.object_type' => 'Event'
        ]);
        $created_id = $this->addEvent($roamingData);
        /**
         * Force set the ID to the one of the new Event, ensuring reload roaming doesn't fail
         * since the original event doesn't exists anymore
         */
        $event['Event']['id'] = $created_id;
        $roamingData->setData($event);
        $this->reloadRoamingData($roamingData);
    }

    public function addEvent(WorkflowRoamingData $roamingData)
    {
        $oldEvent = $roamingData->getData();
        $config = [
            'uuid' => $oldEvent['Event']['uuid'],
            'user_email' => $this->getUserEmailFromEvent($oldEvent),
            'orgc_id' => $oldEvent['Event']['orgc_id'],
        ];
        $baseEvent = $this->getBaseEvent($config);
        $created_id = 0;
        $this->Event->_add($baseEvent, true, $roamingData->getUser(), $baseEvent['Event']['org_id'], null, false, null, $created_id);
        return $created_id;
    }

    private function getUserEmailFromEvent($event)
    {
        return explode(' - ', $event['Event']['info'])[1];
    }

